package prviProjekat;

public class ZadatakZaokruzivanjeVrednosti {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double No = -5.5514;
		
		double Absolut = Math.abs(No);		
		
		System.out.println("Absolutna vrednost broja " + No + " je: " + Absolut);
		
		double Round = Math.round(No);
		
		System.out.println("Zaokruzena vrednost broja " + No + " je: " + Round);
		
	}
}
